with attributes AS(SELECT SalesOrganization, Material, AttributeName, AttributeValue, ABSOLUTEVALUE,ISABSOLUTE FROM IMG_DATA.dbo.TBL_Attributes WITH(NOLOCK) WHERE SalesOrganization = '8030'AND ContentType = 'BASIC')SELECT ('CA01' + '@@' + '8030' + '@@' + '10' + '@@' + 'A300'+ '-'+ P.material) AS x_rec_spec,P.material,attributename,attributevalue,absolutevalue,CASE WHEN isabsolute ='True' THEN 'true' ELSE 'false' END AS isabsolute FROM catalog.materials P with (nolock) INNER JOIN attributes sa ON sa.SalesOrganization = P.SalesOrganization AND sa.material = P.material WHERE P.SalesOrganization = '8030' AND P .WebVisibleFlag = 'WebVisibleFlag' AND  P.DeleteFlag IS NULL Order by P.material